* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  min-height: 100vh;
  font-family: Arial, Helvetica, sans-serif;
  background-color: #3399CC;
  border: 24px solid #2B81AC;
  padding: 32px;
}

.container {
  min-width: 990px;
  margin: 0 auto;
}

.header__title {
  color: #fff;
  font-weight: 500;
}
.header__icon {
  color: #fff;
  font-size: 30px;
  font-weight: 500;
  padding-top: 5px;
  padding-left: 7px;
}

.main {
  padding: 30px 0;
}

.list {
  list-style: none;
  font-size: 0;
  border: 1px solid #fff;
}
.list:hover .list__item:not(:hover):before {
  opacity: 0.4;
}
.list__item {
  border: 3px solid #fff;
  width: 100%;
}
.list__item--1-2 {
  width: 50%;
}
.list__item--1-4 {
  width: 25%;
}
.list__item:nth-last-child(1) {
  background-color: #3399CC;
}
.list__item:nth-last-child(2) {
  background-color: #33CCCC;
}
.list__item:nth-last-child(3) {
  background-color: #996699;
}
.list__item:nth-last-child(4) {
  background-color: #C24747;
}
.list__item:nth-last-child(5) {
  background-color: #E2674A;
}
.list__item:nth-last-child(6) {
  background-color: #FFCC66;
}
.list__item:nth-last-child(7) {
  background-color: #99CC99;
}
.list__item:nth-last-child(8) {
  background-color: #669999;
}
.list__item:nth-last-child(9) {
  background-color: #CC6699;
}
.list__item:nth-last-child(10) {
  background-color: #339966;
}
.list__item:nth-last-child(11) {
  background-color: #666699;
}

.card {
  display: inline-block;
  text-align: center;
  padding: 32px;
  opacity: 0.8;
  cursor: pointer;
  position: relative;
}
.card::before {
  content: "";
  display: block;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
  position: absolute;
  background-color: #fff;
  opacity: 0;
  transition: opacity 0.2s ease;
}
.card__title {
  color: #fff;
  font-size: 22px;
  font-weight: 500;
  margin-bottom: 6px;
}
.card__temp {
  color: #fff;
  opacity: 0.7;
  font-size: 22px;
  font-weight: 300;
  margin-right: 6px;
}
.card__icon {
  font-size: 22px;
  font-weight: 700;
  color: #fff;
  opacity: 0.7;
}

.footer__title {
  color: #fff;
  font-weight: 500;
}

